(148,	32,	'_form',	'<label>Nombre *</label>[text* your-name placeholder \"Nombre\"] \n\n<label>Dirigido a</label><div class=\"styled-select\">\n[select* mailto first_as_label \"Dirigido a\"\n\"Comercio, Ocio, Turismo y Vivienda|dacmail@gmail.com\" \n\"Comisión Permanente|da.cmail@gmail.com\" \n\"Economía y Presupuestos y Participativos|dac.mail@gmail.com\" \n\"Espacio Público, Movilidad y Medio Ambiente|d.acmail@gmail.com\" \n\"Igualdad|dacmail+igualdad@gmail.com\"   \n\"Cultura y deportes|dac@ungrynerd.com\"]\n</div>\n\n<label>Email *</label>[email* your-email placeholder \"Email\"] \n\n<label>Mensaje *</label>[textarea* your-message placeholder \"Mensaje\"] \n\n<div class=\"clearfix\">[submit \"Enviar\"]</div>'),
